import React, { useState } from 'react';
import Navbar from './components/Navbar';
import Motivation from './components/Motivation';
import Timer from './components/Timer';
import Schedule from './components/Schedule';
import './App.css';

function App() {
  const [completedSessions, setCompletedSessions] = useState([]); // ‚úÖ Inisialisasi state

  const handleSessionComplete = () => {
    setCompletedSessions((prev) => [...prev, prev.length]);
  };
  const [darkMode, setDarkMode] = useState(false);
  const Navbar = ({ darkMode, setDarkMode }) => {
    return (
      <div className="navbar">
        <h1>üéØ Fokus Yuk! Pomodoro Timer</h1>
        <button onClick={() => setDarkMode(prev => !prev)}>
          {darkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
        </button>
      </div>
    );
  };


  return (
    <div className="app-container">
      <Navbar />
      <div className="main-content">
        <div className="left-column">
          <Motivation />
          <Timer onSessionComplete={handleSessionComplete} />
        </div>
        <div className="right-column">
          <Schedule completedSessions={completedSessions} /> {/* ‚úÖ Gunakan di sini */}
        </div>
      </div>
      <footer>
        ¬© 2025 Pomodoro Productivity App ‚Äî Dibuat oleh Kak Apri üí°‚ú®
      </footer>
    </div>
  );
}

export default App;
