import React, { useState, useEffect } from 'react';

const Timer = ({ onSessionComplete }) => {
    const [timeLeft, setTimeLeft] = useState(1500); // 25 menit
    const [isActive, setIsActive] = useState(false);
    const [session, setSession] = useState(0);

    useEffect(() => {
        let timer;
        if (isActive && timeLeft > 0) {
            timer = setInterval(() => {
                setTimeLeft(prev => prev - 1);
            }, 1000);
        } else if (isActive && timeLeft === 0) {
            handleSessionComplete(); // Pemanggilan fungsi ketika selesai
        }
        return () => clearInterval(timer);
    }, [isActive, timeLeft]);

    const formatTime = (time) => {
        const minutes = Math.floor(time / 60);
        const seconds = time % 60;
        return `${minutes.toString().padStart(2, '0')}:${seconds
            .toString()
            .padStart(2, '0')}`;
    };

    const handleSessionComplete = () => {
        onSessionComplete(session);
        setSession(session + 1);

        // Auto-restart:
        setTimeout(() => {
            setIsActive(true);
            setTimeLeft(1500); // 25 menit baru
        }, 1000); // delay 1 detik biar tidak terlalu tiba-tiba
    };


    return (
        <div className="timer">
            <h2>Pomodoro Timer</h2>
            <div className="time-display">{formatTime(timeLeft)}</div>
            <div className="controls">
                <button onClick={() => setIsRunning(true)}>Start</button>
                <button onClick={() => setIsRunning(false)}>Pause</button>
                <button onClick={() => { setIsRunning(false); setTimeLeft(25 * 60); }}>Reset</button>
            </div>
        </div>
    );
};

export default Timer;
